  margin-top: 5rem;
  padding: 0 3.5rem;

  &::after {
    content: "";
    display: table;
    clear: both;
  }

  &__btn {
    &--prev {
      float: left;
    }
    &--next {
      float: right;
    }
  }
}

.copyright {
  color: $color-grey-dark-2;
  font-size: 1.2rem;
  padding: 0 3.5rem;
  margin-top: 15rem;

  .linkedin-link:link,
  .linkedin-link:visited {
    color: $color-grey-dark-2;
  }
}

